CREATE TABLE catalog_products (id integer, discount numeric(5,2), price numeric(10,2), quantity bigint, warehouse_product_id bigint not null, seller_id bigint not null, primary key (id));
CREATE TABLE category (id integer, name varchar(255) not null, primary key (id));
CREATE TABLE employees (id integer, hire_date date not null, iin varchar(12) not null unique, salary numeric(10,2) not null, user_id bigint not null unique, primary key (id));
CREATE TABLE order_items (id integer, price numeric(10,2) not null, quantity bigint not null, order_id bigint, product_id bigint, warehouse_id bigint, primary key (id));
CREATE TABLE orders (id integer, delivery_address varchar(255) not null, order_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP, status VARCHAR(20) DEFAULT 'PENDING' check (status in ('PENDING','SENDING','DELIVERED','CANCELLED')), status_update_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP, total numeric(38,2), user_id bigint not null, primary key (id));
CREATE TABLE sales (id integer, quantity bigint not null, timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP not null, product_id bigint not null, user_id bigint, warehouse_id bigint, primary key (id));
CREATE TABLE suppliers (id integer, address varchar(255) not null, email varchar(255) not null, name varchar(255) not null, phone varchar(255) not null, registration_date date not null, user_id bigint not null, primary key (id));
CREATE TABLE users (id integer, address varchar(255) not null, balance numeric(10,2) not null, email varchar(255) not null, name varchar(255) not null, password varchar(255) not null, phone varchar(255) not null unique, role VARCHAR(20) DEFAULT 'USER' not null check (role in ('USER','SUPPLIER','EMPLOYEE','MANAGER','ADMIN')), surname varchar(255) not null, username varchar(255) not null unique, primary key (id));
CREATE TABLE warehouse_products (id integer, base_price numeric(10,2), created TIMESTAMP DEFAULT CURRENT_TIMESTAMP not null, description varchar(255) not null, image varchar(255), name varchar(255) not null, quantity bigint, sku varchar(255) not null unique, category_id bigint not null, warehouse_id bigint, primary key (id));
CREATE TABLE warehouses (id integer, address varchar(255) not null, name varchar(255) not null, employee_id bigint unique, primary key (id));
CREATE TABLE salary_history (id integer, end_date date, salary numeric(10,2) not null, start_date date not null, employee_id bigint not null, primary key (id));
